import React from 'react';
import { IconButton } from '@material-ui/core';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import Stack from '@mui/material/Stack';
import CommentIcon from '@mui/icons-material/Comment';
import CreateComment from './CreateComment';

type Props = {
    commentable: boolean;
    thread_page_id: number;
};

const DeleteEditCommentOnThread: React.FC<Props> = ({ commentable, thread_page_id }: Props) => {
    const [openCreateComment, setOpenCreateComment] = React.useState<boolean>(false);
    const handleClickOpenCreateComment = () => {
      setOpenCreateComment(true);
    };
    const handleCloseCreateComment = () => {
      setOpenCreateComment(false);
    };

    const [openDeleteComment, setOpenDeleteComment] = React.useState<boolean>(false);
    const handleClickOpenDeleteComment = () => {
      setOpenDeleteComment(true);
    };
    const handleCloseDeleteComment = () => {
      setOpenDeleteComment(false);
    };

    return (
        <Stack direction="row" justifyContent='flex-end' alignItems="center" spacing={1}>
            <IconButton>
                <CommentIcon onClick={handleClickOpenCreateComment}/>
            </IconButton>
            <CreateComment thread_page_id={thread_page_id} openCreateComment={openCreateComment} handleCloseCreateComment={handleCloseCreateComment} />
            <IconButton>
                <EditIcon />
            </IconButton>
            <IconButton>
                <DeleteIcon />
            </IconButton>
            <DeleteComment thread_page_id={thread_page_id} openDeleteComment={openDeleteComment} handleCloseDeleteComment={handleCloseDeleteComment} />

        </Stack>
    );
};

export default DeleteEditCommentOnThread;
