import { Typography } from '@material-ui/core';
import Stack from '@mui/material/Stack';

import React from 'react';

type Props = {
    toOverflow: boolean;
    body: string;
}

const BodyAddHTML: React.FC<Props> = ({ body, toOverflow }: Props) => {
    const body_array: string[] = body.split('\n');

    return (
        <Stack direction='column' spacing={0.01}>
            { body_array.map(line => (
                <>
                    <Typography noWrap={toOverflow} align='left' variant="body1">
                        {line}
                    </Typography>
                    <br />
                </>
            ))}
        </Stack>
    );
};

export default BodyAddHTML;
