import '../App.css';
import ThreadMainPost from './ThreadMainPost';
import Stack from '@mui/material/Stack';
import { Thread } from '../types/Thread';
import CurrentUserState from '../types/CurrentUserState';

import React from 'react';

type Props = {
    threads: Thread[];
    currentUserState: CurrentUserState;
    error: any;
    isLoaded: boolean;
    API: string;
}

const ThreadList: React.FC<Props> = ({ threads, error, isLoaded, currentUserState }: Props) => {
    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div style={{ width: '50vw', margin: 'auto', textAlign: 'center' }}>
                <Stack spacing={2}>
                    { threads.map(thread => (
                        <ThreadMainPost API={API} toOverflow={true} thread={thread} key={thread.id} currentUserState={currentUserState}/>
                    ))}
                </Stack>
            </div>
        );
    }
};

export default ThreadList;
