import { Button, TextField, Dialog, DialogActions, DialogContent, 
    DialogTitle, DialogContentText } from '@material-ui/core';
import AlertUser from './AlertUser';
import { AlertColor } from '@mui/material/Alert';

import React, { useState } from 'react';

type Props = {
    openChangePassword: boolean;
    handleCloseChangePassword: () => void;
}

const ChangePassword: React.FC<Props> = ({openChangePassword, handleCloseChangePassword}: Props) => {
    const [newPassword, setPassword] = useState<string>("");
    const [newPasswordConfirmation, setPassword_confirmation] = useState<string>("");
    const [severity, setSeverity] = useState<AlertColor | undefined>(undefined);
    const [message, setMessage] = useState<string[]>([]);

    const resetChangePassword = () => {
        setPassword("");
        setPassword_confirmation("");
    }

    const handleSubmitChangePassword = () => {
        let new_message: string[] = [];
            fetch("http://localhost:3000/users/", {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json",
                  },
                body: JSON.stringify({
                    newPassword,
                    newPasswordConfirmation,
                }),
            })
                .then((response) => {
                    if (response.ok) {
                        setSeverity('success');
                        new_message.push("Account created successfully. Log in to do more!")
                        setMessage(new_message);
                        resetChangePassword();
                        setTimeout(() => window.location.reload(), 1000);
                    }
                    return response.json();
                })
                .then((data) => {
                    if (data.error) {
                        setSeverity('error');
                        data.error.map((error: string) =>
                            new_message.push(error));
                        setMessage(new_message);
                    }
                })
                .catch((error) => console.log(error.message));
        // }
    }

    return (
        <Dialog open={openChangePassword} onClose={handleCloseChangePassword} fullWidth>
            <DialogTitle>Join the ChitChat Community!</DialogTitle>
            <DialogContent>
                <DialogContentText>
                    Dont have an account? Create one to create threads, comment and more...
                </DialogContentText>
                <AlertUser severity={severity} message={message}/>
                    <TextField
                        value={newPassword}
                        type='password'
                        margin="normal"
                        label="Password"
                        fullWidth
                        variant="outlined"
                        onChange={event => setPassword(event.target.value)}
                    />
                    <TextField
                        value={newPasswordConfirmation}
                        type='password'
                        margin="normal"
                        label="Confirm Password"
                        fullWidth
                        variant="outlined"
                        onChange={event => setPassword_confirmation(event.target.value)}
                    />
            </DialogContent>
            <DialogActions>
                <Button onClick={handleCloseChangePassword}>Close</Button>
                <Button onClick={handleSubmitChangePassword}>Create Account</Button>
            </DialogActions>
        </Dialog>
    );
};

export default ChangePassword;
