import ThreadList from '../components/ThreadList';
import { Thread } from '../types/Thread';
import CurrentUserState from '../types/CurrentUserState';

import React, {useState, useEffect} from 'react';

type Props = {
    currentUserState: CurrentUserState;
}

const MyThreads: React.FC<Props> = ( {currentUserState}: Props) => {
    const [error, setError] = useState<any>(null);
    const [isLoaded, setIsLoaded] = useState<boolean>(false);
    const [threads, setThreads] = useState<Thread[]>([]);

    useEffect(() => {
        fetch("http://localhost:3000/mythreads/" + currentUserState.currentUser.username, {
            method: 'GET',
            mode: 'cors'
        })
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setThreads(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, []);

    return (
        <div style={{ width: '50vw', margin: 'auto', textAlign: 'center'}}>
            <ThreadList threads={threads} error={error} isLoaded={isLoaded} currentUserState={currentUserState}/>
        </div>
    );
};

export default MyThreads;
