import CommentList from '../components/CommentList';
import { Button, Card, CardContent, Fade, Typography, Grid } from '@material-ui/core';
import { Link } from 'react-router-dom';
import Header from './Header';
import Avatar from '@mui/material/Avatar';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';

import React from 'react';
import { green100 } from 'material-ui/styles/colors';

const ThreadView: React.FC = () => {
    return (
        <div style={{ width: '50vw', margin: 'auto' }}>

            <Card>
                <CardContent>
                    <Header />
                    <Grid container>
                        {/* Avatar Icon, Username, time/date */}
                        <Grid item container direction='row' xs={6} spacing={5}>
                            <Grid item >
                                <Avatar variant='rounded' sx={{ width: 70, height: 70 }}>J</Avatar>
                            </Grid>
                            <Grid item direction='column'>
                                <Typography align='left' variant='h5'>John</Typography>
                                <Typography align='left' variant='subtitle1'>6h ago</Typography>
                            </Grid>
                        </Grid>
                        <Grid item xs={6} direction='row' spacing={1} justifyContent='flex-end' alignItems='flex-start'>
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                            <Chip label="primary" />
                            <Chip label="success" />
                        </Grid>
                    </Grid>
                    <Typography component="p">{'Viewing thread:'}</Typography>
                    <Typography variant="h5" component="h5">
                        {'Inspirational Quotes'}
                    </Typography>
                    <Typography color="textSecondary" gutterBottom>
                        {'by Aiken'}
                    </Typography>
                    <Typography variant="body2" component="p">
                        {'"The best way to predict the future is to invent it."'}
                        <br />
                        {'- Alan Kay'}
                    </Typography>
                </CardContent>
            </Card>

            <Fade in={true} timeout={1000}>
                <div>
                    <CommentList />
                </div>
            </Fade>

            <Link to="/">
                <Button variant="contained" color="secondary">
                    {'Back to threads'}
                </Button>
            </Link>
        </div>
    );
};

export default ThreadView;
