import Alert, { AlertColor } from '@mui/material/Alert';

import React, { useState } from 'react';

type Props = {
    severity_param: AlertColor | undefined;
    message_param: string[];
}

const AlertUser: React.FC = () => {
    const [severity, setSeverity] = useState<AlertColor | undefined>(undefined);
    const [message, setMessage] = useState<string[]>([]);

    return (
        <>
            { message.map(error =>
                <Alert severity={severity}>{error}</Alert>)  
            }
        </>
    );
};

const setAlertUser = (severity_param, message_param) => {
    setSeverity(severity_param);
    setMessage(message_param);
}

export {AlertUser, setAlertUser};
