import React from 'react';
import { IconButton } from '@material-ui/core';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import Stack from '@mui/material/Stack';
import CommentIcon from '@mui/icons-material/Comment';
import CreateComment from './CreateComment';

type Props = {
    commentable: boolean;
    thread_page_id: number | undefined;
};

const DeleteEditComment: React.FC<Props> = ({ commentable, thread_page_id }: Props) => {
    const [openCreateComment, setOpenCreateComment] = React.useState<boolean>(false);

    const handleClickOpenCreateComment = () => {
      setOpenCreateComment(true);
    };

    const handleCloseCreateComment = () => {
      setOpenCreateComment(false);
    };

    return (
        <Stack direction="row" justifyContent='flex-end' alignItems="center" spacing={1}>
            { commentable &&
                <>
                    <IconButton>
                        <CommentIcon onClick={handleClickOpenCreateComment}/>
                    </IconButton>
                    <CreateComment thread_page_id={thread_page_id} openCreateComment={openCreateComment} handleCloseCreateComment={handleCloseCreateComment} />
                </>
            }
            <IconButton>
                <EditIcon />
            </IconButton>
            <IconButton>
                <DeleteIcon />
            </IconButton>
        </Stack>
    );
};

export default DeleteEditComment;
