import { Button, TextField, Dialog, DialogActions, DialogContent, 
    DialogTitle, Grid, Chip} from '@material-ui/core';
import AlertUser from './AlertUser';
import { AlertColor } from '@mui/material/Alert';

import React, { useState, useEffect } from 'react';

type Props = {
    openCreateThread: boolean;
    handleCloseCreateThread: () => void;
}

const CreateThread: React.FC<Props> = ({openCreateThread, handleCloseCreateThread }: Props) => {
    const [user_id, setUser_id] = useState<number>(1);
    const [title, setTitle] = useState<string>("");
    const [body, setBody] = useState<string>("");
    const [tagsInput, setTagsInput] = useState<string>("");
    const [tagsArray, setTagsArray] = useState<string[]>([]);
    const [severity, setSeverity] = useState<AlertColor | undefined>(undefined);
    const [message, setMessage] = useState<string[]>([]);

    const strToArray = (str: string) => {
        return str.replace(/\s/g, "").toLowerCase().split("#").filter(tag => tag !== "");
    }

    const resetCreateThread = () => {
        setTitle("");
        setBody("");
        setUser_id(1);
        setTagsInput("");
    }

    useEffect(() => {
        setTagsArray(strToArray(tagsInput));
        console.log(tagsArray.toString());
    }, [tagsInput]);

    const handleSubmitCreateThread = () => {
        const tags = tagsArray.toString();
        let new_message: string[] = [];
        if (title === "" || body === "") {
            new_message.push("Fill in all fields");
        }
        if (new_message.length > 0) {
            setSeverity('error');
            setMessage(new_message);
        } else {
            fetch("http://localhost:3000/thread_pages", {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json",
                  },
                body: JSON.stringify({
                    title,
                    body,
                    user_id,
                    tags,
                }),
            })
                .then((response) => {
                    if (response.ok) {
                        setSeverity('success');
                        new_message.push("You have created a new thread!")
                        setMessage(new_message);
                        resetCreateThread();
                        setTimeout(() => window.location.reload(), 1000);
                    } else {
                        setSeverity('error');
                        new_message.push("Some error occured");
                        setMessage(new_message);
                    }
                })
                .catch((error) => console.log(error.message));
        }
    }

    return (
        <Dialog open={openCreateThread} onClose={handleCloseCreateThread} fullWidth>
            <DialogTitle>What's on your mind?</DialogTitle>
            <DialogContent>
                <AlertUser severity={severity} message={message}/>
                <TextField
                    value={title}
                    autoFocus
                    type='text'
                    margin="normal"
                    label="Title"
                    fullWidth
                    variant="standard"
                    onChange={event => setTitle(event.target.value)}
                />
                <TextField
                    value={tagsInput}
                    type='text'
                    margin="normal"
                    label="Tags"
                    fullWidth
                    placeholder='#HawkerFood #Travel #School'
                    variant="standard"
                    onChange={event => setTagsInput(event.target.value)}
                />
                <Grid container xs={12} direction='row' spacing={1} justifyContent='flex-start' alignItems='flex-start'>
                    { tagsArray.length > 0 && tagsArray.map(tag =>
                        <Grid item>
                            <Chip label={tag}/>
                        </Grid>
                    )}
                </Grid>
                <TextField
                    value={body}
                    type='text'
                    margin="normal"
                    label="Say something here..."
                    fullWidth
                    variant="standard"
                    multiline
                    onChange={event => setBody(event.target.value)}
                />
            </DialogContent>
            <DialogActions>
                <Button onClick={handleCloseCreateThread}>Cancel</Button>
                <Button onClick={handleSubmitCreateThread}>Create Thread</Button>
            </DialogActions>
        </Dialog>
    );
};

export default CreateThread;
