import React from 'react';
import { IconButton } from '@material-ui/core';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import Stack from '@mui/material/Stack';

type Props = {
    commentable: boolean;
    thread_page_id: number;
};

const DeleteEditOnComment: React.FC<Props> = ({ commentable, thread_page_id }: Props) => {
    const [openCreateComment, setOpenCreateComment] = React.useState<boolean>(false);

    const handleClickOpenCreateComment = () => {
      setOpenCreateComment(true);
    };

    const handleCloseCreateComment = () => {
      setOpenCreateComment(false);
    };

    return (
        <Stack direction="row" justifyContent='flex-end' alignItems="center" spacing={1}>
            <IconButton>
                <EditIcon />
            </IconButton>
            <IconButton>
                <DeleteIcon />
            </IconButton>
        </Stack>
    );
};

export default DeleteEditOnComment;
