import ThreadView from '../components/ThreadView';
import { useParams } from "react-router-dom";
import CurrentUserState from '../types/CurrentUserState';

import React from 'react';

type Props = {
    currentUserState: CurrentUserState,
    API: string,
}

const ThreadPage: React.FC<Props> = ({currentUserState, API}: Props) => {
    let { threadID }  = useParams();
    return (
        <div style={{ width: '50vw', margin: 'auto', textAlign: 'center', paddingTop: "30px" }}>
            <ThreadView threadID={threadID} currentUserState={currentUserState}/>
        </div>
    );
};

export default ThreadPage;
