import React, { useEffect, useState } from 'react';
import Paper from '@mui/material/Paper';
import InputBase from '@mui/material/InputBase';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import SearchIcon from '@mui/icons-material/Search';
import LibraryAddIcon from '@mui/icons-material/LibraryAdd';
import CreateThread from './CreateThread';
import ThreadSearch from '../types/ThreadSearch';
import { Typography } from '@material-ui/core';

const SearchBar: React.FC = () => {
    const [openCreateThread, setOpenCreateThread] = React.useState<boolean>(false);

    const handleClickOpenCreateThread = () => {
      setOpenCreateThread(true);
    };

    const handleCloseCreateThread = () => {
      setOpenCreateThread(false);
    };

    const [search, setSearch] = useState<string>("");
    const [results, setResults] = useState<ThreadSearch[]>([]);
    const [error, setError] = useState<any>(null);
    const [isLoaded, setIsLoaded] = useState<boolean>(false);

    useEffect(() => {
        setIsLoaded(false);
        const title = search;
        fetch("http://localhost:3000/thread_pages/search/title", {
            method: 'POST',
            mode: 'cors',
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                title,
            }),
        })
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setResults(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [search]);

    return (
        <div style={{paddingTop: '20px', paddingBottom: '20px', position: 'sticky', top: '50px', zIndex: '1100'}}>
            <Paper
            component="form" 
            sx={{ display: 'flex', alignItems: 'center' }}
            >
                <InputBase
                    sx={{ ml: 1, flex: 1 }}
                    placeholder="Who's chatting about what?"
                    value={search}
                    onChange={event => setSearch(event.target.value)}
                />
                <IconButton>
                    <SearchIcon />
                </IconButton>
                <Divider sx={{ height: 28, m: 0.5 }} orientation="vertical" />
                <IconButton onClick={handleClickOpenCreateThread}>
                    <LibraryAddIcon />
                </IconButton>
                <CreateThread openCreateThread={openCreateThread} handleCloseCreateThread={handleCloseCreateThread} />
            </Paper>
            <Paper sx={{justifyContent: "initial"}}>
                { results.map(result =>
                    <Typography key={result.id}>{result.title}</Typography>
                )}
            </Paper>
        </div>
    );
};

export default SearchBar;
