import { Button, TextField, Dialog, DialogActions, DialogContent, 
    DialogTitle, DialogContentText } from '@material-ui/core';
import AlertUser from './AlertUser';
import { AlertColor } from '@mui/material/Alert';

import React, { useState } from 'react';
import { CoronavirusSharp } from '@mui/icons-material';

type Props = {
    openCreateAccount: boolean;
    handleCloseCreateAccount: () => void;
}

const CreateAccount: React.FC<Props> = ({openCreateAccount, handleCloseCreateAccount}: Props) => {
    const [user, setUser] = useState<string>("");
    const [password, setPassword] = useState<string>("");
    const [confirmPassword, setConfirmPassword] = useState<string>("");
    const [severity, setSeverity] = useState<AlertColor | undefined>('success');
    const [message, setMessage] = useState<string[]>([]);

    const resetFormInputs = () => {
        setUser("");
        setPassword("");
        setConfirmPassword("");
    }

    const handleSubmitCreateAccount = () => {
        let new_message: string[] = [];
        // if (password !== confirmPassword) {
        //     new_message.push("Passwords do not match");
        // }
        // if (user === "" || password === "" || confirmPassword === "") {
        //     new_message.push("Fill in all fields");
        // }
        // if (new_message.length > 0) {
        //     setSeverity('error');
        //     setMessage(new_message);
        // } else {
            fetch("http://localhost:3000/users", {
                method: 'POST',
                headers: {
                    "Content-Type": "application/json",
                  },
                body: JSON.stringify({
                    user,
                    password,
                }),
            })
                .then((response) => {
                    if (response.ok) {
                        setSeverity('success');
                        new_message.push("Account created successfully. Log in to do more!")
                        setMessage(new_message);
                        resetFormInputs();
                    } else {
                        setSeverity('error');
                        new_message.push("Some error occured");
                        setMessage(new_message);
                    }
                })
                .catch((error) => console.log(error.message));
        // }
    }

    return (
        <Dialog open={openCreateAccount} onClose={handleCloseCreateAccount} fullWidth>
            <DialogTitle>Join the ChitChat Community!</DialogTitle>
            <DialogContent>
                <DialogContentText>
                    Dont have an account? Create one to create threads, comment and more...
                </DialogContentText>
                <AlertUser severity={severity} message={message}/>
                    <TextField
                        autoFocus
                        value={user}
                        type='text'
                        margin="normal"
                        label="Username"
                        fullWidth
                        variant="outlined"
                        onChange={event => setUser(event.target.value)}
                    />
                    <TextField
                        value={password}
                        type='password'
                        margin="normal"
                        label="Password"
                        fullWidth
                        variant="outlined"
                        onChange={event => setPassword(event.target.value)}
                    />
                    <TextField
                        value={confirmPassword}
                        type='password'
                        margin="normal"
                        label="Confirm Password"
                        fullWidth
                        variant="outlined"
                        onChange={event => setConfirmPassword(event.target.value)}
                    />
            </DialogContent>
            <DialogActions>
                <Button onClick={handleCloseCreateAccount}>Cancel</Button>
                <Button onClick={handleSubmitCreateAccount}>Create Account</Button>
            </DialogActions>
        </Dialog>
    );
};

export default CreateAccount;
