import { Card, CardContent, Typography} from '@material-ui/core';
import Stack from '@mui/material/Stack';
import Header from './Header';
import AvatarUserTimeTags from './AvatarUserTimeTags';
import DeleteEditComment from './DeleteEditComment';
import ThreadMain from '../types/ThreadMain';

type Props = {
    toOverflow: boolean;
    thread_main: ThreadMain;
}

const ThreadMainPost: React.FC<Props> = ( {toOverflow, thread_main}: Props) => {
    return (
        <Card>
        <CardContent>
            <Stack direction='column' spacing={3}>
                <Header title={thread.title}/>
                <AvatarUserTimeTags tagged={true} user={"John"} time={new Date(thread.created_at)}/>
                <Typography noWrap={toOverflow} align='left' variant="body1">
                    {thread.body}
                </Typography>
                <DeleteEditComment commentable={true}/>
            </Stack>
        </CardContent>
        </Card>
    );
};

export default ThreadMainPost;
