import { Button, TextField, Dialog, DialogActions, DialogContent, 
    DialogTitle} from '@material-ui/core';
import AlertUser from './AlertUser';
import { AlertColor } from '@mui/material/Alert';
import Comment from '../types/Comment';

import React, { useState, useEffect } from 'react';

type Props = {
    openUpdateComment: boolean;
    handleCloseUpdateComment: () => void;
    comment: Comment;
}

const UpdateComment: React.FC<Props> = ({openUpdateComment, handleCloseUpdateComment, comment}: Props) => {
    const [isLoaded, setIsLoaded] = useState<boolean>(false);
    const [error, setError] = useState<any>(null);
    const [body, setBody] = useState<string>("");
    const [severity, setSeverity] = useState<AlertColor | undefined>(undefined);
    const [message, setMessage] = useState<string[]>([]);

    const handleSubmitUpdateComment = () => {
        let new_message: string[] = [];
        if (body === "") {
            new_message.push("Fill in all fields");
        }
        if (new_message.length > 0) {
            setSeverity('error');
            setMessage(new_message);
        } else {
            fetch("http://localhost:3000/thread_pages/" + comment.thread_page_id + "/comments/" + comment.id, {
                method: 'PATCH',
                headers: {
                    "Content-Type": "application/json",
                  },
                body: JSON.stringify({
                    body,
                }),
            })
                .then((response) => {
                    if (response.ok) {
                        setSeverity('success');
                        new_message.push("Changes saved!")
                        setMessage(new_message);
                        resetUpdateComment();
                        setTimeout(() => window.location.reload(), 1000);
                    } else {
                        setSeverity('error');
                        new_message.push("Some error occured");
                        setMessage(new_message);
                    }
                })
                .catch((error) => console.log(error.message));
        }
    }

    useEffect(() => {
        fetch("http://localhost:3000/thread_pages/" + comment.thread_page_id + "/comments" + comment.id, {
            method: 'PATCH',
            mode: 'cors'
        })
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setBody(result.body);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [comment]);

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <Dialog open={openUpdateComment} onClose={handleCloseUpdateComment} fullWidth>
                <DialogTitle>What's on your mind?</DialogTitle>
                <DialogContent>
                    <AlertUser severity={severity} message={message}/>
                    <TextField
                        value={body}
                        type='text'
                        margin="normal"
                        label="Say something here..."
                        fullWidth
                        variant="standard"
                        multiline
                        onChange={event => setBody(event.target.value)}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseUpdateComment}>Cancel</Button>
                    <Button onClick={handleSubmitUpdateComment}>Edit Comment</Button>
                </DialogActions>
            </Dialog>
        );
    }
};

export default UpdateComment;
