import '../App.css';
import ThreadMainPost from '../components/ThreadMainPost';
import Stack from '@mui/material/Stack';
import { Thread } from '../types/Thread';
import CurrentUserState from '../types/CurrentUserState';

import React, { useEffect, useState } from 'react';

type Props = {
    threadResults: Thread[];
    currentUserState: CurrentUserState;
}

const MyThreads: React.FC<Props> = ({ threadResults, currentUserState }: Props) => {

    const [error, setError] = useState<any>(null);
    const [isLoaded, setIsLoaded] = useState<boolean>(false);
    const [threads, setThreads] = useState<Thread[]>(threadResults);

    useEffect(() => {
        const user = currentUserState.currentUser.id
        fetch("http://localhost:3000/thread_pages", {
            method: 'POST',
            mode: 'cors',
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                user,
            }),
        })
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setThreads(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [threadResults]);

    if (error) {
        return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div style={{ width: '50vw', margin: 'auto', textAlign: 'center', paddingTop: "30px" }}>
                <Stack spacing={2}>
                    { threads.map(thread => (
                        <ThreadMainPost toOverflow={true} thread={thread} key={thread.id}/>
                    ))}
                </Stack>
            </div>
        );
    }
};

export default MyThreads;
