import ThreadList from '../components/ThreadList';
import { Typography } from '@material-ui/core';
import Typewriter from 'typewriter-effect';
import SearchBar from '../components/SearchBar';
import { Thread } from '../types/Thread';
import CurrentUserState from '../types/CurrentUserState';

import React, {useState} from 'react';

type Props = {
    currentUserState: CurrentUserState;
}

const Home: React.FC<Props> = ( {currentUserState}: Props) => {
    const [threadResults, setThreadResults] = useState<Thread[]>([]);

    return (
        <div style={{ width: '50vw', margin: 'auto', textAlign: 'center'}}>
            <Typography variant='h3' style={{ paddingTop: '70px'}}>
                <Typewriter
                    onInit={(typewriter) => {
                        typewriter
                            .changeDelay(80)
                            .typeString("ChitChat")
                            .pauseFor(1000)
                            .start();
                    }}
                /> 
            </Typography>
            <SearchBar setThreadResults={setThreadResults}/>
            <ThreadList threadResults={threadResults} currentUserState={currentUserState}/>
        </div>
    );
};

export default Home;
