import React from 'react';
import { IconButton } from '@material-ui/core';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import Stack from '@mui/material/Stack';
import DeleteComment from './DeleteComment';
import Comment from '../types/Comment';
import UpdateComment from './UpdateComment';

type Props = {
    comment: Comment;
}

const DeleteEditOnComment: React.FC<Props> = ({comment}: Props) => {
    const [openDeleteComment, setOpenDeleteComment] = React.useState<boolean>(false);
    const handleClickOpenDeleteComment = () => {
      setOpenDeleteComment(true);
    };
    const handleCloseDeleteComment = () => {
      setOpenDeleteComment(false);
    };

    const [openUpdateComment, setOpenUpdateComment] = React.useState<boolean>(false);
    const handleClickOpenUpdateComment = () => {
      setOpenUpdateComment(true);
    };
    const handleCloseUpdateComment = () => {
      setOpenUpdateComment(false);
    };

    return (
        <Stack direction="row" justifyContent='flex-end' alignItems="center" spacing={1}>
            <IconButton>
                <EditIcon onClick={handleClickOpenUpdateComment}/>
            </IconButton>
            <UpdateComment comment={comment} openUpdateComment={openUpdateComment} handleCloseUpdateComment={handleCloseUpdateComment} />
            <IconButton>
                <DeleteIcon onClick={handleClickOpenDeleteComment}/>
            </IconButton>
            <DeleteComment comment={comment} openDeleteComment={openDeleteComment} handleCloseDeleteComment={handleCloseDeleteComment} />
        </Stack>
    );
};

export default DeleteEditOnComment;
