import { Button, TextField, Dialog, DialogActions, DialogContent, 
    DialogTitle } from '@material-ui/core';
import AlertUser from './AlertUser';
import { AlertColor } from '@mui/material/Alert';

import React, { useState } from 'react';

type Props = {
    openDeleteComment: boolean;
    handleCloseDeleteComment: () => void;
    thread_page_id: number;
    comment_id: number;
}

const DeleteComment: React.FC<Props> = ({openDeleteComment, handleCloseDeleteComment, thread_page_id, comment_id}: Props) => {
    const [severity, setSeverity] = useState<AlertColor | undefined>(undefined);
    const [message, setMessage] = useState<string[]>([]);

    const handleSubmitDeleteComment = () => {
        let new_message: string[] = [];
        fetch("http://localhost:3000/thread_pages/" + thread_page_id.toString() + "/comments/" + comment_id, {
            method: 'DELETE',
            headers: {
                "Content-Type": "application/json",
                },
        })
            .then((response) => {
                if (response.ok) {
                    setSeverity('success');
                    new_message.push("You have deleted thread!")
                    setMessage(new_message);
                } else {
                    setSeverity('error');
                    new_message.push("Some error occured");
                    setMessage(new_message);
                }
            })
            .catch((error) => console.log(error.message));
    }

    return (
        <Dialog open={openDeleteComment} onClose={handleCloseDeleteComment} fullWidth>
            <DialogTitle>Are you sure you want to delete this comment?</DialogTitle>
            <DialogContent>
                <AlertUser severity={severity} message={message}/>
            </DialogContent>
            <DialogActions>
                <Button onClick={handleCloseDeleteComment}>Cancel</Button>
                <Button onClick={handleSubmitDeleteComment}>Delete Comment</Button>
            </DialogActions>
        </Dialog>
    );
};

export default DeleteComment;
