import '../App.css';
import ThreadMainPost from './ThreadMainPost';
import Stack from '@mui/material/Stack';
import Thread from '../types/Thread';

import React, { useEffect, useState } from 'react';

const ThreadList: React.FC = () => {
    const threads: Thread[] = [
        {
          id: 1,
          title: "Title",
          body: "You will be making a simple web forum in this assignment. Through the assignment, you will learn about the programming skills required for CVWO’s summer projects and go through a website development cycle. The focus of this assignment is to maximise learning of proper code structure, coding techniques and familiarity with various development tools. You are expected to build up a good web development foundation through this assignment, and prepare yourself for a development cycle during your stint over the summer.",
          user_id: 1,
          created_at: "2022-12-21T08:55:26.168Z",
          updated_at: "2022-12-21T08:55:26.168Z"
        },
        {
          id: 2,
          title: "Title",
          body: "You will be making a simple web forum in this assignment. Through the assignment, you will learn about the programming skills required for CVWO’s summer projects and go through a website development cycle. The focus of this assignment is to maximise learning of proper code structure, coding techniques and familiarity with various development tools. You are expected to build up a good web development foundation through this assignment, and prepare yourself for a development cycle during your stint over the summer.",
          user_id: 1,
          created_at: "2022-12-21T08:55:26.171Z",
          updated_at: "2022-12-21T08:55:26.171Z"
        }
    ];

    const [error, setError] = useState<any>(null);
    const [isLoaded, setIsLoaded] = useState<any>(false);
    const [items, setItems] = useState<any[]>([]);

    useEffect(() => {
        fetch("http://localhost:3000/thread_pages")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsLoaded(true);
                    setItems(result);
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    return (
        <p>{items.toString()}</p>
        // <div style={{ width: '50vw', margin: 'auto', textAlign: 'center' }}>
        //     <Stack spacing={2}>
        //         { items.map(item => (
        //             <ThreadMainPost toOverflow={true} thread={item} key={item['id']}/>
        //         )) }
        //     </Stack>
        // </div>
    );
};

export default ThreadList;
