import Comment from '../types/Comment';
import React from 'react';
import { Card, CardContent, makeStyles, Typography } from '@material-ui/core';
import Stack from '@mui/material/Stack';
import AvatarUserTimeTags from './AvatarUserTimeTags';
import DeleteEditComment from './DeleteEditComment';

type Props = {
    comment: Comment;
};

const useStyles = makeStyles({
    commentBody: {
        fontSize: 16,
        whiteSpace: 'pre-wrap',
        paddingBottom: '1em',
    },
    commentCard: {
        marginBottom: '1em',
    },
    metadata: {
        fontSize: 14,
    },
});

const CommentItem: React.FC<Props> = ({ comment }) => {
    const classes = useStyles();
        return (
            <Card className={classes.commentCard}>
                <CardContent>
                    <Stack direction='column' spacing={3}>
                        <AvatarUserTimeTags tagged={false} user={comment.user} time={comment.time.toString}/>
                        <Typography align='left' variant="body1">                   
                            {comment.body}
                        </Typography>
                        <DeleteEditComment commentable={false} />
                    </Stack>
                    <Typography variant="body2" color="textPrimary" className={classes.commentBody} component="p">
                        {comment.body}
                    </Typography>
                    <Typography color="textSecondary" className={classes.metadata} gutterBottom>
                        {'Posted by ' + comment.user + ' on ' + comment.time.toLocaleString()}
                    </Typography>
                </CardContent>
            </Card>
        );
};

export default CommentItem;
